mapper.select:
		SELECT  FROM sys_role a


               AND a.role_name LIKE concat('%',#{roleName},'%')


               AND a.is_admin = #{isAdmin}


               AND a.status = #{status}

mapper.insert:
INSERT INTO sys_role(
	role_name,
	is_admin,
	status
) VALUES (
	#{roleName},
	#{isAdmin},
	#{status}
)

mapper.update:
UPDATE sys_role SET 
	role_name = #{roleName},
	is_admin = #{isAdmin},
	status = #{status}
WHERE id = #{id}
